    private void btnloginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnloginActionPerformed
        // TODO add your handling code here
        String unm,pnm,s1;

        unm=txtusername.getText();
        pnm=txtpass.getText();

        //login credential matching code here(from database)

        Config conf=new Config();
        conf.connect();
        //will change the code below
        s1="select fname,pass from Teachers where fname='"+unm+"'";
        try{
            //JOptionPane.showMessageDialog(this,"Query done");
            Config.stmt1.executeQuery(s1);//problem here
            Config.rs1=Config.stmt1.getResultSet();

            // JOptionPane.showMessageDialog(this,"Entering condition");
            if(Config.rs1.next())
            {
                if(Config.rs1.getString("pass").equals(pnm))

                {JOptionPane.showMessageDialog(this,"LOGIN SUCCESS");
                    this.dispose();
                    new Feed_n_Read_Data().setVisible(true);
                }

                else
                JOptionPane.showMessageDialog(this,"Invalid password");
            }
            else
            JOptionPane.showMessageDialog(this,"User id does not exist");

        }
        catch(Exception e)
        {
            JOptionPane.showMessageDialog(null,"Failed"+e);
            e.printStackTrace();
        }
    }//GEN-LAST:event_btnloginActionPerformed

